mapper.update:
        update caigoudingdan
        set cgtime =#{cgtime},caigou_jiaohuoren = #{caigou_jiaohuoren},caigou_yanshouren = #{caigou_yanshouren},caigou_jizhangren=#{caigou_jizhangren} ,caigou_beizhu=#{caigou_beizhu} cgdno = #{cgdno}
        where cgdno = #{cgdno};

        update caigou
        set cgtime =#{cgtime},caigou_jiaohuoren = #{caigou_jiaohuoren},caigou_yanshouren = #{caigou_yanshouren},caigou_jizhangren=#{caigou_jizhangren} ,caigou_beizhu=#{caigou_beizhu}, cgdno  = #{cgdno}
        where cgdno = #{cgdno};

mapper.select:
select *
from caigoudingdan
where  cgdno  = #{cgdno} or cgtime = #{cgtime};
